import { createReducer, on } from '@ngrx/store';
import { login, logout } from './user.actions';
const initialState = {
  currentUser: null,
  loggedIn: false
};
export const userReducer = createReducer(initialState, on(login, state => {
  console.log('login state: ' + JSON.stringify(state));
  return {
    ...state,
    loggedIn: true
  };
}), on(logout, state => {
  console.log('logout state: ' + JSON.stringify(state));
  return {
    ...state,
    loggedIn: false
  };
}));
